/**
 * A proxy handler for exposing array-valued hashes
 * as hashes with singular values.
 * This proxy will always take the first element of array values.
 */
export declare class SingularPropertyHandler<T> implements ProxyHandler<Record<string, T[]>> {
    has(target: Record<string, T[]>, propertyKey: PropertyKey): boolean;
    get(target: Record<string, T[]>, propertyKey: PropertyKey): T | undefined;
    set(target: Record<string, T[]>, propertyKey: PropertyKey, value: any): boolean;
    ownKeys(target: Record<string, T[]>): PropertyKey[];
    deleteProperty(target: Record<string, T[]>, propertyKey: PropertyKey): boolean;
}
